import React, { useState, useEffect } from "react";
import Weatherinfo from "./componentt/weather/weatherinfo";

import "./style.css"
const Temp = () => {
    const [searchValue,setSearchValue]=useState("solan");
    const [tempInfo,setTempInfo]= useState({});

    const getWeatherInfo= async()=>{
        try{
            let url= `https://api.openweathermap.org/data/2.5/weather?q=${searchValue}&units=metric&appid=f44fd6894d11bc0a5b7c3aa4f3f77606`;

            const res=await fetch(url);
            const data=await res.json();

            const { temp ,humidity,pressure } =data.main;
            const {main: weathermood} =data.weather[0];
            const {name}=data;
            const {speed} =data.wind;
            const {country,sunset}=data.sys;

            const myNewWeatherInfo={
                temp,
                humidity,
                pressure,
                weathermood,
                name,
                speed,
                country,
                sunset,
            };

        setTempInfo( myNewWeatherInfo);

            
        } catch(error){
            console.log(error);
        }
    };

    useEffect(()=>{
        getWeatherInfo();
    },[]);
    return (
    <>
        <div className="wrap">
            <div className="search">
                <input
                    type="search"
                    placeholder="search..."
                    autoFocus
                    id="search"
                           className="searchTerm" 
                           value={ searchValue}
                           onChange={(e)=> setSearchValue(e.target.value)} 
                           />

                <button
                    className="searchButton"
                    type="button"
                    onClick={getWeatherInfo}>
                    Search
                </button>
            </div>
        </div>
      
      

    

    </>
    )
}

export default Temp
